INSERT INTO `mdw_system_registry` VALUES (1,'mainTitle','La Familia es Prioridad'),(2,'mainTitleSeparator',' - '),(3,'menuTreeUl','<ul id=\"topMenu\" class=\"sf-menu\"><li><a href=\"\">Inicio</a></li><li><a href=\"page/quienesSomos\">¿Quienes somos?</a><ul><li><a href=\"page/principios\">Principios doctrinales</a></li><li><a href=\"page/biografia\">Biografí­a del fundador</a></li></ul></li><li><a>Cápsula Radial</a><ul><li><a href=\"page/programaFamilia\">¿Por qué un programa para la familia?</a></li><li><a href=\"page/capsulaRadial\">¿Por qué un micro programa o cápsula radial?</a></li><li><a href=\"page/necesidad\">¿Por qué se necesita producir La Familia es Prioridad?</a></li><li><a href=\"page/comoColaborar\">¿Cómo puedo ser parte del proyecto?</a></li></ul></li><li><a>Conferencias y Seminarios</a><ul><li><a href=\"page/cenasParejas\">Cenas de Parejas</a></li><li><a href=\"page/predicas\">Predicaciones sobre la familia</a></li><li><a href=\"page/tenerEventos\">¿Cómo puedo tener un evento?</a></li></ul></li><li><a>Programas para emisoras</a><ul><li><a href=\"contact/registro\">Solicitud de inscripción</a></li><li><a href=\"page/serieMatrimonios\">Matrimonios</a></li><li><a href=\"page/serieVida\">Vida Familiar</a></li><li><a href=\"page/seriePaternidad\">Paternidad Responsable</a></li><li><a href=\"page/serieDesafios\">Desafí­os de la Familia Contemporanea</a></li><li><a href=\"page/serieEllasEllos\">Ellas y ellos, contrarios pero complementarios</a></li><li><a href=\"page/serieFinanzas\">La familia que administra bien sus finanzas</a></li><li><a href=\"page/serieHijos\">Proverbios antiguos para familias modernas</a></li><li><a href=\"page/serieErroresPadres\">Errores que debemos evitar los padres</a></li><li><a href=\"page/serieErroresMatrimonio\">Errores que debemos evitar en el matrimonio</a></li><li><a href=\"page/serieEtapas\">Etapas naturales de la vida familiar</a></li><li><a href=\"page/serieAdicciones\">Adicciones</a></li><li><a href=\"page/serieHabitos\">Hábitos</a></li><li><a href=\"page/serieRefranesVidaFamiliar\">Refranes aplicados a la vida familiar</a></li><li><a href=\"page/serieCrisisFamilia\">Superando las crisis familiares</a></li><li><a href=\"page/serieSexualidad\">Sexualidad</a></li><li><a href=\"page/serieHeridas\">Heridas</a></li><li><a href=\"page/serieCantares\">Cantares, un canto al amor conyugal</a></li></ul></li></ul>'),(4,'ga','UA-21870325-1');
